import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Blog = () => {
  const [posts, setPosts] = useState([]);
  const authToken = localStorage.getItem('authToken'); // Assuming you store the authentication token in local storage

  useEffect(() => {
    axios.get('http://localhost:3001/posts', {
      headers: {
        Authorization: `Bearer ${authToken}`,
        Accept: 'application/json', // Specify JSON as the expected response
      },
    })
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
  }, [authToken]);

  return (
    <div className="blog">
      <h2>Latest Blog Posts</h2>
      <div className="post-list">
        {posts.length === 0 ? (
          <p>No posts available.</p>
        ) : (
          posts.map((post, index) => (
            <div key={index} className="post">
              <h3 className="post-title">{post.title}</h3>
              <p className="post-author">By {post.author}</p>
              <div className="post-content">{post.content}</div>
              <div className="post-images">
                {post.images.map((image, imgIndex) => (
                  <img
                    key={imgIndex}
                    src={image.url} // Assuming your API provides the image URL from Active Storage
                    alt={`Image ${imgIndex + 1}`}
                    className="post-image"
                  />
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Blog;
