import React, { useState, useEffect } from 'react';

// Main App Component
function App() {
  const [tab, setTab] = useState('blog');
  const [posts, setPosts] = useState([]);
  const [selectedPost, setSelectedPost] = useState(null);

  useEffect(() => {
    // Fetch posts from API
    fetch('https://s3-us-west-2.amazonaws.com/s.cdpn.io/110131/posts_1.json')
      .then(response => response.json())
      .then(data => setPosts(data));
  }, []);

  const selectTab = (tabName) => {
    setTab(tabName);
    setSelectedPost(null); // Clear selected post when switching tabs
  };

  const showPost = (index) => {
    setSelectedPost(index);
  };

  const addComment = (postIndex, comment) => {
    // Add comment to the selected post
    const updatedPosts = [...posts];
    updatedPosts[postIndex].comments.push(comment);
    setPosts(updatedPosts);
  };

  return (
    <div>
      <div className="topbar">
        {/* ... Tab selection navigation ... */}
      </div>

      <div className="content">
        {selectedPost === null ? (
          <BlogList posts={posts} tab={tab} selectTab={selectTab} showPost={showPost} />
        ) : (
          <IndividualPost post={posts[selectedPost]} addComment={(comment) => addComment(selectedPost, comment)} />
        )}
      </div>
    </div>
  );
}

// Blog List Component
function BlogList({ posts, tab, selectTab, showPost }) {
  return (
    <ul className="post-grid" style={{ display: tab === 'blog' ? 'block' : 'none' }}>
      {posts.map((post, index) => (
        <li key={index} className="col-s-4" onClick={() => showPost(index)}>
          {/* ... Blog post details ... */}
        </li>
      ))}
    </ul>
  );
}

// Individual Blog Post Component
function IndividualPost({ post, addComment }) {
  return (
    <div className="post">
      {/* ... Individual post details ... */}
      <CommentSection comments={post.comments} addComment={addComment} />
    </div>
  );
}

// Comment Section Component
function CommentSection({ comments, addComment }) {
  // ... Comment rendering and input form ...
}

export default App;
