import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Blogs.css';
// import ThumbsUpIcon from './ThumbsUpIcon'; // Import ThumbsUpIcon component
import ThumbsDownIcon from './ThumbsDownIcon'; // Import ThumbsDownIcon component
import ThumbsUpIcon from './ ThumbsUpIcon';   



const Blog = () => {
    const [posts, setPosts] = useState([]);
    const authToken = localStorage.getItem('authToken'); // Assuming you store the authentication token in local storage
    const apiBaseUrl = 'http://localhost:3001'; // Replace with your actual API base URL

    useEffect(() => {
      axios.get(`${apiBaseUrl}/posts`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
          Accept: 'application/json', // Specify JSON as the expected response
        },
      })
      .then(response => {
        console.log('API Response Data:', response.data); // Log the entire response for debugging
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
    }, [authToken, apiBaseUrl]);


    const handleLikePost = (postId) => {
      axios.post(`${apiBaseUrl}/posts/${postId}/like`, null, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      })
      .then(response => {
        // Handle success, e.g., update the UI to reflect the liked post
      })
      .catch(error => {
        console.error('Error liking post:', error);
      });
    };

    // Function to handle disliking a post
    const handleDislikePost = (postId) => {
      axios.post(`${apiBaseUrl}/posts/${postId}/dislike`, null, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      })
      .then(response => {
        // Handle success, e.g., update the UI to reflect the disliked post
      })
      .catch(error => {
        console.error('Error disliking post:', error);
      });
    };
    return (
      <div className="blog">
        <h2>Latest Blog Posts</h2>
        <div className="post-list">
          {posts.map((post, index) => (
            <div key={index} className="post">
              <h3 className="post-title">{post.title}</h3>
              <p className="post-author">By {post.author}</p>
              <div className="post-content">{post.content}</div>
              {post.image_url && (
                <img
                  src={`${apiBaseUrl}${post.image_url}`} // Use the absolute image URL
                  alt={post.title} // Set an appropriate alt text
                  className="post-image"
                  onLoad={() => console.log('Image loaded successfully')}
                  onError={() => console.error('Image loading error')}
                />
              )}
              {/* Thumbs Up Icon */}
              <label className="container">
                <input checked="checked" type="checkbox" />
                <svg viewBox="0 0 512 512" height="1em" xmlns="http://www.w3.org/2000/svg" className="thumbs-up-regular">
                  <path d="M323.8 34.8c-38.2-10.9-78.1 11.2-89 49.4l-5.7 20c-3.7 13-10.4 25-19.5 35l-51.3 56.4c-8.9 9.8-8.2 25 1.6 33.9s25 8.2 33.9-1.6l51.3-56.4c14.1-15.5 24.4-34 30.1-54.1l5.7-20c3.6-12.7 16.9-20.1 29.7-16.5s20.1 16.9 16.5 29.7l-5.7 20c-5.7 19.9-14.7 38.7-26.6 55.5c-5.2 7.3-5.8 16.9-1.7 24.9s12.3 13 21.3 13L448 224c8.8 0 16 7.2 16 16c0 6.8-4.3 12.7-10.4 15c-7.4 2.8-13 9-14.9 16.7s.1 15.8 5.3 21.7c2.5 2.8 4 6.5 4 10.6c0 7.8-5.6 14.3-13 15.7c-8.2 1.6-15.1 7.3-18 15.1s-1.6 16.7 3.6 23.3c2.1 2.7 3.4 6.1 3.4 9.9c0 6.7-4.2 12.6-10.2 14.9c-11.5 4.5-17.7 16.9-14.4 28.8c.4 1.3 .6 2.8 .6 4.3c0 8.8-7.2 16-16 16H286.5c-12.6 0-25-3.7-35.5-10.7l-61.7-41.1c-11-7.4-25.9-4.4-33.3 6.7s-4.4 25.9 6.7 33.3l61.7 41.1c18.4 12.3 40 18.8 62.1 18.8H384c34.7 0 62.9-27.6 64-62c14.6-11.7 24-29.7 24-50c0-4.5-.5-8.8-1.3-13c15.4-11.7 25.3-30.2 25.3-51c0-6.5-1-12.8-2.8-18.7C504.8 273.7 512 257.7 512 240c0-35.3-28.6-64-64-64l-92.3 0c4.7-10.4 8.7-21.2 11.8-32.2l5.7-20c10.9-38.2-11.2-78.1-49.4-89zM32 192c-17.7 0-32 14.3-32 32V448c0 17.7 14.3 32 32 32H96c17.7 0-32-14.3-32-32V224c0-17.7-14.3-32-32-32H32z"></path>
                </svg>
                {/* Thumbs Up Icon */}
              </label>
              <button onClick={() => handleDislikePost(post.id)}>Dislike</button>
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default Blog;
