import React, { useState, useEffect } from "react";
import { useNavigation } from "@react-navigation/native"; // Import useNavigation from your routing library
import axios from "axios";

const Login = ({ onToggleForm }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const navigation = useNavigation(); // Initialize useNavigation

  useEffect(() => {
    // Check if the user is already logged in (e.g., based on a stored token)
    const authToken = localStorage.getItem("authToken");

    if (authToken) {
      // If the user is logged in, redirect to another route (e.g., "Dashboard")
      navigation.navigate("Dashboard"); // Use navigate to redirect
    }
  }, [navigation]);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:3001/auth/sign_in",
        {
          email,
          password,
          remember_me: rememberMe,
        }
      );

      if (response.status === 200) {
        const authToken = response.headers["access-token"];
        const client = response.headers["client"];
        const uid = response.headers["uid"];

        localStorage.setItem("authToken", authToken);
        localStorage.setItem("client", client);
        localStorage.setItem("uid", uid);

        console.log("Login successful");

        // Redirect to the Dashboard route
        navigation.navigate("Dashboard");
      } else {
        console.log("Login failed");
      }
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  return (
    <div className="auth-form bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-semibold mb-4 text-blue-600">Login</h2>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-600">
            Email
          </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 p-2 border rounded border-solid border-blue-400 w-full focus:border-blue-400 focus:outline-none"
            required
          />
        </div>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-600">
            Password
          </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 p-2 border rounded border-solid border-blue-400 w-full focus:border-blue-400 focus:outline-none"
            required
          />
        </div>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-600">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-1"
            />
            Remember me
          </label>
        </div>
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded transition duration-300 shadow-md"
        >
          Login
        </button>
      </form>
      <p className="mt-4">
        Don't have an account?{" "}
        <button
          onClick={onToggleForm}
          className="text-blue-500 hover:underline focus:outline-none"
        >
          Sign Up
        </button>
      </p>
    </div>
  );
};

export default Login;
