import React, { useState } from 'react';
import Login from './login'; // Ensure the import is correct
import Signup from './signup'; // Ensure the import is correct
import './AuthComponent.css'; // Create this CSS file for styling

const AuthComponent = ({ setToken }) => {
  const [showLogin, setShowLogin] = useState(true);

  const handleToggleForm = () => {
    setShowLogin(!showLogin);
  };

  return (
    <div className="auth-container">
      <div className={`auth-form-container ${showLogin ? 'login' : 'signup'}`}>
        {showLogin ? (
          <Login onToggleForm={handleToggleForm} setToken={setToken} />
        ) : (
          <Signup onToggleForm={handleToggleForm} setToken={setToken} />
        )}
      </div>
    </div>
  );
};

export default AuthComponent;
