const Blog = () => {
  const [posts, setPosts] = useState([]);
  const authToken = localStorage.getItem("authToken");
  const apiBaseUrl = "http://localhost:3001";

  useEffect(() => {
    const fetchPostsData = async () => {
      try {
        // Fetch blog posts
        const response = await axios.get(`${apiBaseUrl}/posts`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
            Accept: "application/json",
          },
        });

        // Fetch like and dislike counts for each post
        const updatedPosts = await Promise.all(
          response.data.map(async (post) => {
            const likeResponse = await axios.get(
              `${apiBaseUrl}/posts/${post.id}/likes`
            ); // Use the new "likes" route
            const dislikesResponse = await axios.get(
              `${apiBaseUrl}/posts/${post.id}/dislikes`
            ); // Use the new "dislikes" route

            return {
              ...post,
              likes_count: likeResponse.data.likesCount,
              dislikes_count: dislikesResponse.data.dislikesCount,
            };
          })
        );

        setPosts(updatedPosts);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPostsData();
  }, [authToken, apiBaseUrl]);

  const handleLikePost = async (postId) => {
    try {
      // Send a POST request to like the post
      await axios.post(`${apiBaseUrl}/posts/${postId}/like`, null, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });

      console.log("Post liked successfully");

      // Update the UI to reflect the new like count
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId
            ? {
                ...post,
                likes_count: post.likes_count + 1,
              }
            : post
        )
      );
    } catch (error) {
      console.error("Error liking post:", error);
    }
  };

  const handleDislikePost = async (postId) => {
    try {
      // Send a POST request to dislike the post
      await axios.post(`${apiBaseUrl}/posts/${postId}/dislike`, null, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });

      console.log("Post disliked successfully");

      // Update the UI to reflect the new dislike count
      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId
            ? {
                ...post,
                dislikes_count: post.dislikes_count + 1,
              }
            : post
        )
      );
    } catch (error) {
      console.error("Error disliking post:", error);
    }
  };

  return (
    <div className="blog">
      <h2>Latest Blog Posts</h2>
      <div className="post-list">
        {posts.map((post, index) => (
          <div key={index} className="post">
            <h3 className="post-title">{post.title}</h3>
            <p className="post-author">By {post.author}</p>
            <div className="post-content">{post.content}</div>
            {post.image_url && (
              <img
                src={`${apiBaseUrl}${post.image_url}`}
                alt={post.title}
                className="post-image"
                onLoad={() => console.log("Image loaded successfully")}
                onError={() => console.error("Image loading error")}
              />
            )}
            <div>
              <button onClick={() => handleLikePost(post.id)}>
                <i className="fa fa-thumbs-up thumbs-up-icon"></i>{" "}
                <span>{post.likes_count}</span>
              </button>
              <button onClick={() => handleDislikePost(post.id)}>
                <i className="fa fa-thumbs-down thumbs-down-icon"></i>{" "}
                <span>{post.dislikes_count}</span>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
