import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ onToggleForm, onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/auth/sign_in', {
        email,
        password,
      });

      if (response.status === 200) {
        const authToken = response.headers['access-token'];

        // Store the Authorization header with the Bearer token in localStorage
        localStorage.setItem('authorization', `Bearer ${authToken}`);

        // Log the token to the console to check if it's working
        console.log('Authorization Header:', `Bearer ${authToken}`);

        // Fetch the user's details using the token
        const userResponse = await axios.get('http://localhost:3001/user', {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        // Extract and store the user's name
        const userName = userResponse.data.name;
        localStorage.setItem('userName', userName);

        // Notify the parent component about the login
        onLogin(userName);

        // Redirect to the home page or another page of your choice
        navigate('/');

      } else {
        console.log('Login failed');
      }
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleLogout = () => {
    // Clear the stored token and user name
    localStorage.removeItem('authorization');
    localStorage.removeItem('userName');

    // Notify the parent component about the logout
    onLogin(null);
  };

  // Check if there's an existing token in localStorage
  // and if so, automatically navigate to the home page.
  const checkExistingToken = () => {
    const authToken = localStorage.getItem('authorization');
    if (authToken) {
      // Fetch and display the user's name if available
      const userName = localStorage.getItem('userName');
      onLogin(userName);

      console.log('User is already logged in. Token found:', authToken);
      navigate('/');
    }
  };

  // Call checkExistingToken when the component mounts
  React.useEffect(() => {
    checkExistingToken();
  }, []);

  return (
    <div className="auth-form bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-semibold mb-4 text-blue-600">Login</h2>
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-600">Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mt-1 p-2 border rounded border-solid border-blue-400 w-full focus:border-blue-400 focus:outline-none"
            required
          />
        </div>
        <div className="mb-3">
          <label className="block text-sm font-medium text-gray-600">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="mt-1 p-2 border rounded border-solid border-blue-400 w-full focus:border-blue-400 focus:outline-none"
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded transition duration-300 shadow-md"
        >
          Login
        </button>
      </form>
      <p className="mt-4">
        Don't have an account?{' '}
        <button onClick={onToggleForm} className="text-blue-500 hover:underline focus:outline-none">
          Sign Up
        </button>
      </p>
      {localStorage.getItem('authorization') && (
        <p>
          Logged in as: {localStorage.getItem('userName')}{' '}
          <button onClick={handleLogout} className="text-red-500 hover:underline focus:outline-none">
            Logout
          </button>
        </p>
      )}
    </div>
  );
};

export default Login;
