import React, { useState, useEffect } from 'react';
// import { AiFillLike, AiFillDislike } from 'react-icons/ai';

import axios from 'axios';
import './Blogs.css';

const Blog = () => {
  const [posts, setPosts] = useState([]);
  const authToken = localStorage.getItem('authToken'); // Assuming you store the authentication token in local storage
  const apiBaseUrl = 'http://localhost:3001'; // Replace with your actual API base URL

  useEffect(() => {
    axios.get(`${apiBaseUrl}/posts`, {
      headers: {
        Authorization: `Bearer ${authToken}`,
        Accept: 'application/json', // Specify JSON as the expected response
      },
    })
    .then(response => {
      console.log('API Response Data:', response.data); // Log the entire response for debugging
      setPosts(response.data);
    })
    .catch(error => {
      console.error('Error fetching posts:', error);
    });
  }, [authToken, apiBaseUrl]);


  const handleLikePost = (postId) => {
    axios.post(`${apiBaseUrl}/posts/${postId}/like`, null, {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
    .then(response => {
      // Handle success, e.g., update the UI to reflect the liked post
    })
    .catch(error => {
      console.error('Error liking post:', error);
    });
  };

  // Function to handle disliking a post
  const handleDislikePost = (postId) => {
    axios.post(`${apiBaseUrl}/posts/${postId}/dislike`, null, {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
    .then(response => {
      // Handle success, e.g., update the UI to reflect the disliked post
    })
    .catch(error => {
      console.error('Error disliking post:', error);
    });
  };

  // return (
  //   <div className="blog">
  //     <h2>Latest Blog Posts</h2>
  //     <div className="post-list">
  //       {posts.map((post, index) => (
  //         <div key={index} className="post">
  //           <h3 className="post-title">{post.title}</h3>
  //           <p className="post-author">By {post.author}</p>
  //           <div className="post-content">{post.content}</div>
  //           {post.image_url && (
  //             <img
  //               src={`${apiBaseUrl}${post.image_url}`} // Use the absolute image URL
  //               alt={post.title} // Set an appropriate alt text
  //               className="post-image"
  //               onLoad={() => console.log('Image loaded successfully')} // Log when the image is loaded
  //               onError={() => console.error('Image loading error')} // Log if there's an error loading the image
  //             />
  //           )}
  //           <button onClick={() => handleLikePost(post.id)}>Like</button>
  //           <button onClick={() => handleDislikePost(post.id)}>Dislike</button>
  //         </div>
  //       ))}
  //     </div>
  //   </div>
  // );
  return (
    <div className="blog">
      <h2>Latest Blog Posts</h2>
      <div className="post-list">
        {posts.map((post, index) => (
          <div key={index} className="post">
            <h3 className="post-title">{post.title}</h3>
            <p className="post-author">By {post.author}</p>
            <div className="post-content">{post.content}</div>
            {post.image_url && (
              <img
                src={`${apiBaseUrl}${post.image_url}`}
                alt={post.title}
                className="post-image"
                onLoad={() => console.log('Image loaded successfully')}
                onError={() => console.error('Image loading error')}
              />
            )}
            {/* Custom Like and Dislike structure */}
            <div className="like-dislike-container">
              <div className="tool-box">
                <button className="btn-close">×</button>
              </div>
              <p className="text-content">What did you think<br />of this post?</p>
              <div className="icons-box">
                <div className="icons">
                  <label className="btn-label" htmlFor={`like-checkbox-${post.id}`}>
                    <span className="like-text-content">123</span>
                    <input className="input-box" id={`like-checkbox-${post.id}`} type="checkbox" />
                    <svg className="svgs" id="icon-like-solid" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                      {/* Your SVG path for Like here */}
                    </svg>
                    {/* Your fireworks animation here */}
                  </label>
                </div>
                <div className="icons">
                  <label className="btn-label" htmlFor={`dislike-checkbox-${post.id}`}>
                    <input className="input-box" id={`dislike-checkbox-${post.id}`} type="checkbox" />
                    {/* Your fireworks animation here */}
                    <svg className="svgs" id="icon-dislike-solid" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                      {/* Your SVG path for Dislike here */}
                    </svg>
                    <span className="dislike-text-content">4</span>
                  </label>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
