import React, { useState, useEffect } from 'react';
import './Art.css';
import BlogCard from './BlogCard';
import axios from 'axios';

const Education = ({ categoryId }) => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    // Define the API URL with the dynamic category ID
    const apiUrl = `http://localhost:3001/categories/${categoryId}/posts`;

    // Use Axios to fetch data from the backend
    axios.get(apiUrl)
      .then(response => {
        const data = response.data;
        console.log('Fetched data:', data); // Log the fetched data
        setBlogs(data);
      })
      .catch(error => {
        console.error('Error fetching blogs:', error); // Log any errors
      });
  }, [categoryId]);

  console.log('Rendered with blogs:', blogs); // Log the current state of blogs

  return (
    <div className='category-wrapper'>
      {blogs.map(blog => (
        <BlogCard key={blog.id} category='Education' {...blog} />
      ))}
    </div>
  );
};

export default Education;
