import React, { useState } from 'react';
import { FaUser, FaBars } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav className={`navbar ${menuOpen ? 'menu-open' : ''}`}>
      <div className="nav-left">
        <Link to="/" className="nav-logo">iBlog.</Link>
      </div>
      <div className="nav-toggle" onClick={toggleMenu}>
        <FaBars className="menu-icon" />
      </div>
      <ul className={`nav-list ${menuOpen ? 'menu-open' : ''}`}>
        <li className="nav-item"><Link to="/" className="nav-link">Home</Link></li>
        <li className="nav-item"><Link to="/about" className="nav-link">About</Link></li>

        <li className="nav-item"><Link to="/portfolio" className="nav-link">Portfolio</Link></li>
        <li className="nav-item"><Link to="/createpost" className="nav-link">Create</Link></li>
      </ul>
      <div className="nav-right">
        <Link to="/auth" className="nav-link">
          <FaUser className="user-icon" />
        </Link>
      </div>
    </nav>
  );
}

export default Navbar;
