  import React, { useState, useEffect } from "react";
  import Dropzone from "react-dropzone";
  import axios from "axios";
  import "./createpost.css";

  const CreatePost = ({ currentUser }) => {
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [category, setCategory] = useState("");
    const [categories, setCategories] = useState([]);
    const [images, setImages] = useState([]);
    const [userPosts, setUserPosts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(3); // Change this to set the number of posts per page

    useEffect(() => {
      // Fetch categories from the backend and set them to the state
      axios
        .get("http://localhost:3001/categories")
        .then((response) => {
          setCategories(response.data);
        })
        .catch((error) => {
          console.error("Error fetching categories:", error);
        });

      // Fetch the logged-in user's posts from the backend
      if (currentUser) {
        const authToken = localStorage.getItem("authToken");
        const userId = currentUser.id; // Check if currentUser is defined

        axios
          .get(`http://localhost:3001/users/${userId}/posts`, {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          })
          .then((response) => {
            setUserPosts(response.data);
          })
          .catch((error) => {
            console.error("Error fetching user posts:", error);
          });
      }
    }, [currentUser]);

    const handleImageDrop = (acceptedFiles) => {
      setImages(acceptedFiles);
    };

    const handleSubmit = async (event) => {
      // ... (same as your existing code)

      // Reset form fields
      setTitle("");
      setContent("");
      setCategory("");
      setImages([]);
    };

    // Calculate the index of the last post on the current page
    const indexOfLastPost = currentPage * postsPerPage;
    // Calculate the index of the first post on the current page
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    // Get the posts to display on the current page
    const currentPosts = userPosts.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = (pageNumber) => {
      setCurrentPage(pageNumber);
    };

    return (
      <div className="create-post">
        <h2>Create a New Post</h2>
        {/* Create Post form */}

        <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
        <Dropzone onDrop={handleImageDrop}>
          {({ getRootProps, getInputProps }) => (
            <div className="dropzone" {...getRootProps()}>
              <input {...getInputProps()} />
              <p>Drag & drop images or click here to select files</p>
            </div>
          )}
        </Dropzone>
        <br />
        <button class="fancy" href="#">
          <span class="top-key"></span>
          <span class="text">Create Post</span>
          <span class="bottom-key-1"></span>
          <span class="bottom-key-2"></span>
        </button>
      </form>

        {/* Render the user's posts in cards */}
        <div className="posts">
          <h3>Your Posts</h3>
          {userPosts.length === 0 ? (
            <p>No blogs, start your blogging journey</p>
          ) : (
            <div className="blog-cards-container">
              <ul>
                {currentPosts.map((post) => (
                  <li key={post.id}>
                    <div className="blog-card">
                      <h4>{post.title}</h4>
                      <p>{post.content}</p>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
          {/* Pagination */}
          <ul className="pagination">
            {Array.from({ length: Math.ceil(userPosts.length / postsPerPage) }).map(
              (_, index) => (
                <li key={index}>
                  <button onClick={() => paginate(index + 1)}>{index + 1}</button>
                </li>
              )
            )}
          </ul>
        </div>
      </div>
    );
  };

  export default CreatePost;
