// export default Art;
import React, { useState, useEffect } from 'react';

function Art() {
  const [posts, setPosts] = useState([]);
  const [tab, setTab] = useState('blog');
  const [currentPost, setCurrentPost] = useState(null);

  useEffect(() => {
    fetch('https://s3-us-west-2.amazonaws.com/s.cdpn.io/110131/posts_1.json')
      .then(response => response.json())
      .then(data => setPosts(data));
  }, []);

  const selectTab = (tabName) => {
    setTab(tabName);
    setCurrentPost(null);
  };

  const showPost = (index) => {
    setCurrentPost(index);
  };

  const addComment = (postIndex, comment) => {
    const updatedPosts = [...posts];
    updatedPosts[postIndex].comments.push(comment);
    setPosts(updatedPosts);
  };

  return (
    <div>
      <div className="topbar">
        <div className="container">
          <div className="row">
            <div className="col-s-4">
              <h1 onClick={() => selectTab('blog')} className="push-left">AngularJS Blog App</h1>
            </div>
            <div className="offset-s-4 col-s-4">
              <nav role='navigation' className="push-right">
                <ul>
                  <li><a href="#" onClick={() => selectTab('blog')}>See All Posts</a></li>
                  <li><a href="#" onClick={() => selectTab('new')}>Add New Post</a></li>
                </ul>
              </nav> 
            </div>
          </div>
        </div>
      </div>

      <div className="content">
        <div className="container">
          <div className="row">
            <ul className="post-grid" style={{ display: tab === 'blog' ? 'block' : 'none' }}>
              {posts.map((post, index) => (
                <li key={index} className="col-s-4" onClick={() => showPost(index)}>
                  {/* ... Blog post details ... */}
                </li>
              ))}
            </ul>
            {currentPost !== null && (
              <div className="post">
                {/* ... Individual post details ... */}
                {/* ... Comment section ... */}
              </div>
            )}
            {/* ... New post form ... */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Art;
