import React, { useState, useEffect } from "react";
import axios from "axios";

const Post = ({ post, authToken, apiBaseUrl }) => {
  const [userAction, setUserAction] = useState(null);

  useEffect(() => {
    // Fetch user's action (like/dislike) for the post
    axios
      .get(`${apiBaseUrl}/posts/${post.id}/user_action`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
          Accept: "application/json",
        },
      })
      .then((response) => {
        const { user_action } = response.data;
        setUserAction(user_action);
      })
      .catch((error) => {
        console.error("Error fetching user action:", error);
      });
  }, [authToken, apiBaseUrl, post.id]);

  const handleLike = () => {
    if (userAction === "like") {
      // User already liked, remove the like
      axios
        .delete(`${apiBaseUrl}/posts/${post.id}/like`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        })
        .then(() => {
          setUserAction(null);
        })
        .catch((error) => {
          console.error("Error removing like:", error);
        });
    } else {
      // User dislikes, change to like
      axios
        .post(`${apiBaseUrl}/posts/${post.id}/like`, null, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        })
        .then(() => {
          setUserAction("like");
        })
        .catch((error) => {
          console.error("Error liking post:", error);
        });
    }
  };

  const handleDislike = () => {
    if (userAction === "dislike") {
      // User already disliked, remove the dislike
      axios
        .delete(`${apiBaseUrl}/posts/${post.id}/dislike`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        })
        .then(() => {
          setUserAction(null);
        })
        .catch((error) => {
          console.error("Error removing dislike:", error);
        });
    } else {
      // User likes, change to dislike
      axios
        .post(`${apiBaseUrl}/posts/${post.id}/dislike`, null, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        })
        .then(() => {
          setUserAction("dislike");
        })
        .catch((error) => {
          console.error("Error disliking post:", error);
        });
    }
  };

  return (
    <div className="post">
      <h3 className="post-title">{post.title}</h3>
      <p className="post-author">By {post.author}</p>
      <div className="post-content">{post.content}</div>
      {post.image_url && (
        <img
          src={`${apiBaseUrl}${post.image_url}`} // Use the absolute image URL
          alt={post.title} // Set an appropriate alt text
          className="post-image"
          onLoad={() => console.log("Image loaded successfully")} // Log when the image is loaded
          onError={() => console.error("Image loading error")} // Log if there's an error loading the image
        />
      )}
      <div>
        <button onClick={handleLike}>
          <i className={`fa fa-thumbs-up ${userAction === "like" ? "active" : ""}`}></i>{" "}
          <span>{post.like_count}</span>
        </button>
        <button onClick={handleDislike}>
          <i className={`fa fa-thumbs-down ${userAction === "dislike" ? "active" : ""}`}></i>{" "}
          <span>{post.dislike_count}</span>
        </button>
      </div>
    </div>
  );
};

export default Post;
