<div className="blog">
      <h2>Latest Blog Posts</h2>
      <div className="post-list">
        {posts.map((post, index) => (
          <div
            key={index}
            className={`post ${post.id === expandedPostId ? "expanded" : ""}`}
          >
            <h3 className="post-title">{post.title}</h3>
            <p className="post-author">By {post.author}</p>
            <div className="post-content">
              {post.id === expandedPostId ? (
                // Display the expanded post content
                <div>
                  <p className="modal-title">{post.title}</p>
                  <p className="modal-author">By {post.author}</p>
                  <p className="modal-content-text">{post.content}</p>
                </div>
              ) : (
                // Display a portion of content if not expanded
                <div>
                  {post.content.slice(0, 200)}
                  {post.content.length > 200 && (
                    <span
                      className="read-more"
                      onClick={() => handlePostClick(post.id)}
                    >
                      Read More
                    </span>
                  )}
                </div>
              )}
            </div>
            {post.image_url && (
              <img
                src={`${apiBaseUrl}${post.image_url}`}
                alt={post.title}
                className="post-image"
                onLoad={() => console.log("Image loaded successfully")}
                onError={() => console.error("Image loading error")}
              />
            )}
            <div>
              <button onClick={() => handleLikePost(post.id)}>
                <i className="fa fa-thumbs-up thumbs-up-icon"></i>{" "}
                <span>{post.likes_count}</span>
              </button>
              <button onClick={() => handleDislikePost(post.id)}>
                <i className="fa fa-thumbs-down thumbs-down-icon"></i>{" "}
                <span>{post.dislikes_count}</span>
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal Overlay */}
      {expandedPostId && (
        <div className={`modal-overlay ${expandedPostId ? "" : "modal-hidden"}`}>
          {/* Modal Content */}
          <div className="modal-content">
            <span className="modal-close" onClick={() => setExpandedPostId(null)}>
              &times;
            </span>
            {posts.map((post) => (
              post.id === expandedPostId && (
                <div key={post.id}>
                  <p className="modal-title">{post.title}</p>
                  <p className="modal-author">By {post.author}</p>
                  <p className="modal-content-text">{post.content}</p>
                </div>
              )
            ))}
          </div>
        </div>
      )}
    </div>
  );
};


export default Blog;
