import React, { useState, useEffect } from "react";
import "font-awesome/css/font-awesome.min.css";
import axios from "axios";
import "./Blogs.css";

const Blog = () => {
  const [posts, setPosts] = useState([]);
  const [expandedPostId, setExpandedPostId] = useState(null);
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState("");
  const authToken = localStorage.getItem("authToken");
  const apiBaseUrl = "http://localhost:3001";

  useEffect(() => {
    // Fetch blog posts without comments initially
    axios
      .get(`${apiBaseUrl}/posts`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
          Accept: "application/json",
        },
      })
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  }, [authToken, apiBaseUrl]);

  useEffect(() => {
    // Fetch comments when a blog post is expanded
    if (expandedPostId !== null) {
      axios
        .get(`${apiBaseUrl}/posts/${expandedPostId}/comments`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
            Accept: "application/json",
          },
        })
        .then((response) => {
          setComments({ ...comments, [expandedPostId]: response.data });
        })
        .catch((error) => {
          console.error("Error fetching comments:", error);
        });
    }
  }, [expandedPostId, authToken, apiBaseUrl, comments]);

  const handleLikePost = async (postId) => {
    // ...
  };

  const handleDislikePost = async (postId) => {
    // ...
  };

  const handlePostClick = (postId) => {
    // ...
  };

  const handleCommentSubmit = async (postId) => {
    try {
      // Send a POST request to add a new comment
      await axios.post(
        `${apiBaseUrl}/posts/${postId}/comments`,
        { comment: newComment },
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
        }
      );

      // Fetch updated comments and update the state
      const commentResponse = await axios.get(
        `${apiBaseUrl}/posts/${postId}/comments`,
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
            Accept: "application/json",
          },
        }
      );

      setComments({ ...comments, [postId]: commentResponse.data });
      setNewComment(""); // Clear the input field after submission
    } catch (error) {
      console.error("Error adding comment:", error);
    }
  };

  return (
    <div className="blog">
      <h2>Latest Blog Posts</h2>
      <div className="post-list">
        {posts.map((post, index) => (
          <div
            key={index}
            className={`post ${post.id === expandedPostId ? "expanded" : ""}`}
          >
            {/* ... (existing code) */}

            {/* Display comments section */}
            {post.id === expandedPostId && (
              <div className="comments-section">
                <h4>Comments:</h4>
                <ul>
                  {comments[post.id] &&
                    comments[post.id].map((comment, commentIndex) => (
                      <li key={commentIndex}>{comment.text}</li>
                    ))}
                </ul>
                <div>
                  <input
                    type="text"
                    placeholder="Add a comment..."
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                  />
                  <button onClick={() => handleCommentSubmit(post.id)}>
                    Add Comment
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
