// export default CreatePost;
import React, { useState, useEffect } from "react";
import Dropzone from "react-dropzone";
import axios from "axios";
import "./createpost.css";

const CreatePost = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [categories, setCategories] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Fetch categories from the backend and set them to the state
    axios
      .get("http://localhost:3001/categories")
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  const handleImageDrop = (acceptedFiles) => {
    setImages(acceptedFiles);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("category", category);
    images.forEach((image) => formData.append("images[]", image));

    // Retrieve the user's authentication token from local storage
    const authToken = localStorage.getItem('authToken');
    console.log('Auth Token:', authToken); // Add this line to log the authToken


    const headers = {
      'Authorization': `Bearer ${authToken}`,
      'Content-Type': 'multipart/form-data', // Set the content type for file uploads
    };

    try {
      const response = await axios.post(
        "http://localhost:3001/posts",
        formData,
        { headers }
      );
      console.log("Post created:", response.data);
      // Reset form fields
      setTitle("");
      setContent("");
      setCategory("");
      setImages([]);
    } catch (error) {
      console.error("Error creating post:", error);
    }
  };

  return (
    <div className="create-post">
      <h2>Create a New Post</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
        <Dropzone onDrop={handleImageDrop}>
          {({ getRootProps, getInputProps }) => (
            <div className="dropzone" {...getRootProps()}>
              <input {...getInputProps()} />
              <p>Drag & drop images or click here to select files</p>
            </div>
          )}
        </Dropzone>
        <br />
        <button class="fancy" href="#">
          <span class="top-key"></span>
          <span class="text">Create Post</span>
          <span class="bottom-key-1"></span>
          <span class="bottom-key-2"></span>
        </button>
      </form>
    </div>
  );
};

export default CreatePost;
