import React, { useState, useEffect } from 'react';
import './Art.css'; // Update the CSS file name
import axios from 'axios';

const Education = () => {
  const [data, setData] = useState([]);
  const [expandedCard, setExpandedCard] = useState(null); // Track the expanded card's index

  // Define the togglePostVisibility function
  const togglePostVisibility = (index) => {
    if (expandedCard === index) {
      // If the clicked card is already expanded, close it
      setExpandedCard(null);
    } else {
      // Expand the clicked card and close others
      setExpandedCard(index);
    }
  };

  useEffect(() => {
    // Fetch data from the backend for the "Education" category
    axios
      .get(`http://localhost:3001/categories/10/posts`)
      .then((response) => {
        const educationData = response.data.map((blog) => ({
          id: blog.id,
          title: blog.title,
          content: blog.content,
          image: blog.image_url,
        }));
        setData(educationData);
      })
      .catch((error) => {
        console.error('Error fetching education data:', error);
      });

    // Fetch data from the backend for the "Blog" category
    axios
      .get(`http://localhost:3001/categories/10/posts`)
      .then((response) => {
        const blogData = response.data.map((blog) => ({
          id: blog.id,
          title: blog.title,
          content: blog.content,
          image: blog.image_url,
        }));
        // Combine the data from both categories
        setData((prevData) => [...prevData, ...blogData]);
      })
      .catch((error) => {
        console.error('Error fetching blog data:', error);
      });
  }, []);

  return (
    <div className="education-wrapper"> {/* Update the class name */}
      {data.map((item, index) => (
        <div
          className={`blog-card ${expandedCard === index ? 'expanded' : ''}`}
          key={item.id}
          onClick={() => togglePostVisibility(index)} // Toggle visibility on click
        >
          <h2>{item.title}</h2>
          <div className={`post-content ${expandedCard === index ? 'visible' : ''}`}>
            <p>{expandedCard === index ? item.content : item.content.slice(0, 200) + '...'}</p>
            {item.image && (
              <img src={item.image} alt={`Image for ${item.title}`} className="blog-image" />
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Education;
