// import React from "react";
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./App.css";
import Home from "./components/Home/Home";
import Navbar from "./components/Navbar/Navbar";
import Footer from "./components/Footer/Footer";
import AuthComponent from "./components/register/AuthComponent";
import Sidebar from "./components/Sidebar/Sidebar";
import Blog from "./components/Blogs/Blogs";
import CreatePost from "./components/createpost/createpost";
import Health from './components/Categories/Health';
import Travel from './components/Categories/Travel';
import Music from './components/Categories/Music';
import Art from './components/Categories/Art';
import Education from './components/Categories/Education';
import Fashion from './components/Categories/Fashion'
import Other from './components/Categories/Other'
import Science from './components/Categories/Science'
import Sports from './components/Categories/Sports'
import Technology from './components/Categories/Technology'
import ProtectedRoute from './components/ProtectedRoute'; // Import the ProtectedRoute component


function App() {
  const [token, setToken] = useState();




  const onLogin = (userToken) => {
    // You can perform any actions you need when a user logs in.
    // For example, you can set the token in state.
    setToken(userToken);
  }

  return (
    <Router>
      <div className="App">
        <Sidebar />
        <Navbar />

        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/auth"
            element={<AuthComponent setToken={setToken} />}
          />
          <Route path="/blog" element={<Blog />} />
          {/* <Route
            path="/createpost"
            element={<ProtectedRoute token={token} element={CreatePost} />}
          /> */}
                     <Route path="/createpost" element={<CreatePost />} />

          <Route
            path="/category/health"
            element={<ProtectedRoute token={token} element={Health} />}
          />
          <Route
            path="/category/travel"
            element={<ProtectedRoute token={token} element={Travel} />}
          />
          <Route
            path="/category/music"
            element={<ProtectedRoute token={token} element={Music} />}
          />
          <Route
            path="/category/art"
            element={<ProtectedRoute token={token} element={Art} />}
          />
          <Route
            path="/category/education"
            element={<ProtectedRoute token={token} element={Education} />}
          />
          <Route
            path="/category/fashion"
            element={<ProtectedRoute token={token} element={Fashion} />}
          />
          <Route
            path="/category/other"
            element={<ProtectedRoute token={token} element={Other} />}
          />
          <Route
            path="/category/science"
            element={<ProtectedRoute token={token} element={Science} />}
          />
          <Route
            path="/category/sports"
            element={<ProtectedRoute token={token} element={Sports} />}
          />
          <Route
            path="/category/technology"
            element={<ProtectedRoute token={token} element={Technology} />}
          />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
